class TestWebScraper(TestCase):

    def setUp(self):
        self.web_scraper = WebScraper()
        self.requestsMock = Mock()
        self.web_scraper.requests = self.requestsMock

    def test_get_public_procurements_number_list_status_not_200(self):
        response_mock = Mock()
        response_mock.status_code = 404
        response_mock.content = CONTENT_OK
        self.requestsMock.get.return_value = response_mock
        with self.assertRaises(Exception) as context:
            self.web_scraper.get_public_procurements_number_list("02")
        self.assertEqual('Code error 404 on page http://www.conseildetat.be/?lang=fr&page=lastmonth_02',
                         str(context.exception))
        self.requestsMock.get.assert_called_with('http://www.conseildetat.be/?lang=fr&page=lastmonth_02')

    def test_get_public_procurements_number_list_section_missing(self):
        response_mock = Mock()
        response_mock.status_code = 200
        response_mock.content = CONTENT_NOT_OK
        self.requestsMock.get.return_value = response_mock
        with self.assertRaises(MissingSectionException) as context:
            self.web_scraper.get_public_procurements_number_list("02")
        self.assertEqual(
            'Section "Marchés et travaux publics" not found in http://www.conseildetat.be/?lang=fr&page=lastmonth_02',
            str(context.exception))
        self.requestsMock.get.assert_called_with('http://www.conseildetat.be/?lang=fr&page=lastmonth_02')

    def test_get_public_procurements_number_list(self):
        response_mock = Mock()
        response_mock.status_code = 200
        response_mock.content = CONTENT_OK
        self.requestsMock.get.return_value = response_mock
        ref_list = self.web_scraper.get_public_procurements_number_list("02")
        self.requestsMock.get.assert_called_with('http://www.conseildetat.be/?lang=fr&page=lastmonth_02')
        self.assertEqual(REF_LIST, ref_list)

    def test_get_months_all(self):
        self.assertEqual(["01", "02", "03", "04", "05", "06", "07", "08", "09", "10", "11", "12"],
                         self.web_scraper.get_months())

    def test_get_months_after06(self):
        self.assertEqual(["06", "07", "08", "09", "10", "11", "12"],
                         self.web_scraper.get_months(6))

    def test_get_months_after11(self):
        self.assertEqual(["11", "12"],
                         self.web_scraper.get_months(11))


if __name__ == '__main__':
    unittest.main()
